import {useState, useEffect} from 'react';
import Todos from './components/todos/Todos'
import AddPopUp from './components/addPopUp/AddPopUp';

const API_BASE = 'http://localhost:3001'




function App() {

  const [todos, setTodos] = useState([]); // 
  const [popupActive, setPopupActive] = useState(false);
  const [newTodo, setNewTodo] = useState('');



const addPopUpStateHandler = () => {
  setPopupActive(true);
}

useEffect(() => {
  getTodos();
  console.log(todos)
 }, []) //Is used for side effects. This is only called once when component mounts since the second argument is an empty dependancy array.



 const getTodos = () => { // Gets the todo data from mongoDB database.
  fetch(`${API_BASE}/todos`)
  .then(res => res.json())
  .then(data => setTodos(data))
  .catch(err => console.error('Error:', err))
 }


// --- Checking off todo --- //

 const markTodoHandler = async id => { // fetches data of select todo
  console.log('clicked')
  const data = await fetch(`${API_BASE}/todo/complete/${id}`) // we use await to get the data after the response has processed in the server.
  .then(res => res.json()) // we use aysnch await below to precess data since we did not use another .then() to handle the processed data from the response
  .catch(err => console.error('Error: ', err))

  setTodos(prevTodos => prevTodos.map(todo => { // mapping over todos getting the prev state
    if(todo._id === data._id) { // checking for selected todo
      todo.complete = data.complete // changing selected todo complete property
    }
    return todo // returns the update todos.
  }))
}



// --- Deleting Todos --- //

const deleteTodoHandler = async id => {
  const data = await fetch (`${API_BASE}/todo/delete/${id}`,{
    method: 'DELETE'
  })
  .then(res => res.json())
  .catch(err => console.error('Error: ', err))

  setTodos(prevTodos => prevTodos.filter(todo => todo._id !== data._id)); //return todos whose ID does not equal the id of the deleted todo

}

// --- Adding a Todo --- //

const addTodoHandler = async () => { 
  const data = await fetch(`${API_BASE}/todo/new`, {
    method: 'POST',
    headers: {
      "Content-Type": 'application/json' // sets the content type header of the request. It informs the server that the req body contains JSON DATA
    },
    body: JSON.stringify({ // converts the todo value (a string) into JSON data.
      text: newTodo
    })
  }).then(res => res.json());

  setTodos([...todos, data]); // adding the new data object to the current array of todo objects.
  setPopupActive(false);
  setNewTodo('');
}



  return (
    <div className="App">
      <h1>Welcome Daniel</h1>
      <h4>Your tasks</h4>

<Todos 
deleteTodoHandler={deleteTodoHandler}
markTodoHandler={markTodoHandler}
todos={todos}/>


{/* make todos pop-up component  */}
{/* <div className='addPopUp' onClick={() => {setPopupActive(true);}}>+</div> */}
<AddPopUp
addPopUpState={addPopUpStateHandler} />

{/* make pop-up modal component  */}
{popupActive ? (
  <div className='popUp'>
    <div className='closePopUp' onClick={ () => {
      setPopupActive(false);}}>x</div>
    <div className='content'>

      <h3>Add task</h3>
      <input type="text" 
      className='add-todo-input'
      onChange={ e => {setNewTodo(e.target.value)}}
      value={newTodo} /> {/*value is bound to newTodo value*/}
      <div className="button" onClick={() => addTodoHandler()}>Create Task</div>

    </div>
  </div>
): ''}
    </div>
  );
}

export default App;
