import {useState, useEffect} from 'react';

const API_BASE = 'http://localhost:3001'




function App() {

  const [todos, setTodos] = useState([]); // 
  const [popupActive, setPopupActive] = useState(false);
  const [newTodo, setNewTodo] = useState('');


useEffect(() => {
  getTodos();
  console.log(todos)
 }, []) //Is used for side effects. This is only called once when component mounts since the second argument is an empty dependancy array.

 const getTodos = () => { // Gets the todo data from mongoDB database.
  fetch(`${API_BASE}/todos`)
  .then(res => res.json())
  .then(data => setTodos(data))
  .catch(err => console.error('Error:', err))
 }


 const todoOnClickHandler  = async id => {
  const data = fetch(`${API_BASE}/todo/complete/${id}`)
  .then(res => res.json())
  .catch(err => console.error('Error: ', err))

  setTodos(todos => todos.map(todo => {
    if(todo._id === data._id) {
      todo.complete = data.complete
    }
    return todo
  }))
}


  return (
    <div className="App">
      <h1>Welcome Daniel</h1>
      <h4>Your tasks</h4>

      <div className="todos">
        {todos.map(todo => (
                <div className={`todo ${todo.complete ? 'is-complete' : ''}` } 
                key={todo._id}
                onClick={() => todoOnClickHandler(todo._id)} >
                <div className="check-box"></div>
                <div className="text">{todo.text}</div>
                <div className="delete-todo">x</div>
              </div>
        ))}
      </div>
    </div>
  );
}

export default App;
