          <div className="text">Hover</div>
          <div className="delete-todo">x</div>
        </div>

        <div className="todo">
          <div className="check-box"></div>
          <div className="text">Dusting</div>
          <div className="delete-todo">x</div>
        </div>


      </div>
    </div>
  );
}

export default App;
